    {
        $stmt = $this-&gt;pdo-&gt;prepare('update users set funds = :funds where id = :id');
        $stmt-&gt;bindValue(':id', $id, SQLITE3_INTEGER);
        $stmt-&gt;bindValue(':funds', $funds, SQLITE3_INTEGER);
        return $stmt-&gt;execute();
    }

